from kivy.app import Appfrom kivy.uix.widget import Widgetfrom kivy.core.window import Windowfrom kivy.lang.builder import Builderfrom kivy.core.spelling import SpellingWindow.size = (350, 550)Builder.load_file('./spell.kv')class SpellWidget(Widget):    def check_word(self):        try:            spell = Spelling()            spell.select_language('en-US')            word = self.ids.user_word.text            suggestions = spell.suggest(word)            prev_word = ''            for suggestion in suggestions:                prev_word = f'{prev_word}\n{suggestion}'            self.ids.suggested_words.text = prev_word        except ValueError:            self.ids.suggested_words.text = "Type a word"class SpellApp(App):    def build(self):        return SpellWidget()if __name__ == '__main__':    SpellApp().run()